mapper.update:
update drug
set address = #{address,jdbcType=VARCHAR},
  price = #{price,jdbcType=DECIMAL},
  `name` = #{name,jdbcType=VARCHAR},
  description = #{description,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, address, price, `name`, description
from drug
where id = #{id,jdbcType=BIGINT}

